import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

export default function Product() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulated product list
    const products = [
        { id: 1, name: 'Luxury Matte Lipstick Set', price: 34.99, image: 'data:image/jpeg;base64,', category: 'makeup' },
        { id: 2, name: 'Hydrating Night Serum', price: 49.99, image: 'https://dr.rashel.in/cdn/shop/files/Vitamincserum-01_1_11zon_1080x.jpg?v=1722516132', category: 'skincare' },
        { id: 3, name: 'Floral Eau de Parfum', price: 59.99, image: 'data:image/jpeg;base64,', category: 'fragrance' },
        { id: 4, name: 'Vitamin C Brightening Cream', price: 39.99, image: 'https://cloudinary.images-iherb.com/image/upload/f_auto,q_auto:eco/images/adc/adc00880/l/80.jpg', category: 'skincare' },
        { id: 5, name: 'Eyeshadow Palette', price: 45.99, image: 'data:image/jpeg;base64,', category: 'makeup' },
        { id: 6, name: 'Bamboo Makeup Brushes', price: 29.99, image: 'data:image/jpeg;base64,', category: 'accessories' },
    ];

    const found = products.find(p => p.id === parseInt(id));
    setProduct(found || null);
    setLoading(false);
  }, [id]);

  const handleAddToCart = () => {
    const existingCart = JSON.parse(localStorage.getItem('cart')) || [];
    const exists = existingCart.find(item => item.id === product.id);

    let updatedCart;
    if (exists) {
      updatedCart = existingCart.map(item =>
        item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
      );
    } else {
      updatedCart = [...existingCart, { ...product, quantity: 1 }];
    }

    localStorage.setItem('cart', JSON.stringify(updatedCart));
    alert('Product added to cart!');
  };

  const handleAddToWishlist = () => {
    const wishlist = JSON.parse(localStorage.getItem('wishlist')) || [];
    const exists = wishlist.find(item => item.id === product.id);

    if (!exists) {
      wishlist.push(product);
      localStorage.setItem('wishlist', JSON.stringify(wishlist));
      alert('Product added to wishlist!');
    } else {
      alert('Already in wishlist.');
    }
  };

  if (loading) return <div className="container py-5 text-center">Loading...</div>;
  if (!product) return <div className="container py-5 text-center"><h2 className="text-danger">Product not found</h2></div>;

  return (
    <div className="container py-5">
      <div className="row g-4">
        <div className="col-md-6">
          <img src={product.image} alt={product.name} className="img-fluid rounded shadow-sm" />
        </div>
        <div className="col-md-6">
          <h1 className="fw-bold">{product.name}</h1>
          <p className="text-primary fw-bold h4">${product.price.toFixed(2)}</p>
          <p className="text-muted">Category: <strong>{product.category}</strong></p>
          <div className="d-grid gap-2 mt-4">
            <button className="btn btn-primary btn-lg" onClick={handleAddToCart}>
              Add to Cart
            </button>
            <button className="btn btn-outline-danger" onClick={handleAddToWishlist}>
              Add to Wishlist
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
