import { useEffect, useState } from 'react';
import { useLocation, Link, useNavigate } from 'react-router-dom';

function useQuery() {
  return new URLSearchParams(useLocation().search);
}

export default function Products() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentUser, setCurrentUser] = useState(JSON.parse(localStorage.getItem('currentUser')));
  const query = useQuery();
  const navigate = useNavigate();
  const category = query.get('category');
  const search = query.get('search')?.toLowerCase() || '';

  useEffect(() => {
    setLoading(true);

    // Simulate API fetch
    setTimeout(() => {
      const allProducts = [
        { id: 1, name: 'Luxury Matte Lipstick Set', price: 34.99, image: 'data:image/jpeg;base64,', category: 'makeup' },
        { id: 2, name: 'Hydrating Night Serum', price: 49.99, image: 'https://dr.rashel.in/cdn/shop/files/Vitamincserum-01_1_11zon_1080x.jpg?v=1722516132', category: 'skincare' },
        { id: 3, name: 'Floral Eau de Parfum', price: 59.99, image: 'data:image/jpeg;base64,', category: 'fragrance' },
        { id: 4, name: 'Vitamin C Brightening Cream', price: 39.99, image: 'https://cloudinary.images-iherb.com/image/upload/f_auto,q_auto:eco/images/adc/adc00880/l/80.jpg', category: 'skincare' },
        { id: 5, name: 'Eyeshadow Palette', price: 45.99, image: 'data:image/jpeg;base64,', category: 'makeup' },
        { id: 6, name: 'Bamboo Makeup Brushes', price: 29.99, image: 'data:image/jpeg;base64,', category: 'accessories' },
        { id: 7, name: 'Matte Liquid Lipstick',price: 12.99,image: 'data:image/jpeg;base64,', category: 'makeup'},
        { id: 8, name: 'Volumizing Mascara',price: 14.99,image: 'data:image/jpeg;base64,',category:'makeup'},
        { id: 9, name: 'Blush Palette',price: 18.50,image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS4YIyjaAPXVvRnBYFg9XjS6qTlJhGsbwMuXw&s',category:'makeup'},
        { id: 10, name: 'Foundation Stick',price: 16.99,image: 'data:image/jpeg;base64,',category:'makeup'},
        { id: 11, name: 'Eau de Parfum - Floral Bliss', price: 35.00, image: 'data:image/jpeg;base64,', category:'fragrance'},
        { id: 12, name: 'Citrus Body Mist',price: 18.00,image: 'data:image/jpeg;base64,', category:'fragrance'},
        { id: 13, name: 'Vanilla Musk Perfume', price: 28.99,image: 'https://www.bathandbodyworks.in/on/demandware.static/-/Sites-bathandbody_master_catalog/default/dwd70e5072/large/026533846.jpg', category:'fragrance'} 
      ];

      let filtered = allProducts;

      if (category) {
        filtered = filtered.filter(product => product.category === category);
      }

      if (search) {
        filtered = filtered.filter(product =>
          product.name.toLowerCase().includes(search)
        );
      }

      setProducts(filtered);
      setLoading(false);
    }, 1000);
  }, [category, search]);

  const formatTitle = () => {
    if (category && search) {
      return `Results for "${search}" in ${category}`;
    }
    if (search) {
      return `Results for "${search}"`;
    }
    if (category) {
      return `${category.charAt(0).toUpperCase() + category.slice(1)} Products`;
    }
    return 'All Products';
  };

  const addToCart = (product) => {
    const user = JSON.parse(localStorage.getItem('currentUser'));
    if (!user) {
      alert('Please login to add to cart.');
      navigate('/login');
      return;
    }

    const cart = JSON.parse(localStorage.getItem('cart')) || [];
    const existing = cart.find(item => item.id === product.id);
    if (existing) {
      existing.quantity += 1;
    } else {
      cart.push({ ...product, quantity: 1 });
    }
    localStorage.setItem('cart', JSON.stringify(cart));
    alert('Added to cart!');
  };

  const addToWishlist = (product) => {
    const user = JSON.parse(localStorage.getItem('currentUser'));
    if (!user) {
      alert('Please login to add to wishlist.');
      navigate('/login');
      return;
    }

    const wishlist = JSON.parse(localStorage.getItem('wishlist')) || [];
    const exists = wishlist.find(item => item.id === product.id);
    if (!exists) {
      wishlist.push(product);
      localStorage.setItem('wishlist', JSON.stringify(wishlist));
      alert('Added to wishlist!');
    } else {
      alert('Already in wishlist');
    }
  };

  return (
    <div className="container py-5">
      <h1 className="fw-bold mb-4">{formatTitle()}</h1>

      {loading ? (
        <div className="text-center py-5">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      ) : (
        <div className="row g-4">
          {products.length > 0 ? (
            products.map(product => (
              <div key={product.id} className="col-md-4 col-lg-3">
                <div className="card h-100 border-0 shadow-sm product-card">
                  <Link to={`/product/${product.id}`}>
                    <img
                      src={product.image}
                      alt={product.name}
                      className="card-img-top object-fit-cover"
                      style={{ height: '250px' }}
                    />
                  </Link>
                  <div className="card-body">
                    <h5 className="card-title">{product.name}</h5>
                    <p className="card-text text-dark fw-bold">${product.price.toFixed(2)}</p>
                    <div className="d-grid gap-2">
                      <button className="btn btn-outline-light" style={{ backgroundColor: '#8A2BE2', border: 'none' }} onClick={() => addToCart(product)}>Add to Cart</button>
                      <button className="btn btn-outline-light" style={{ backgroundColor: '#8A2BE2', border: 'none' }} onClick={() => addToWishlist(product)}>Add to Wishlist</button>
                    </div>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <div className="col-12 text-center">
              <p className="text-muted">No products found.</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
}
